import React, {useState, useEffect} from 'react';


function App() {
  return (
    <div >
      <HooksStyleComponent/>
    </div>
  );
}

const HooksStyleComponent = () => {
    const [value, setValue] = useState(0)
    const [visible, setVisible] = useState(true)

    if (visible) {
      return (
        <div>
          <button
            onClick={() => setValue((v)=> v+1)}>+ </button>
          <button
            onClick></button>
        </div>
      )
    }
}

export default App;
