    const [visible, setVisible] = useState(true)

    if (visible) {
      return (
        <div>
          <button
            onClick={() => setValue((v)=> v+1)}>+ </button>
            <button
            onClick={() => setValue((v)=> v-1)}>-</button>
          <button
            onClick={()=> setVisible(false)}> hide </button>
            <PlanetInfo id={value} />
        </div>
      )
    } else {
      return(
        <button
          onClick={()=> setVisible(true)}>Show</button>
