import React, {useState} from 'react';


function App() {
  return (
    <div >
      <HooksStyleComponent/>
    </div>
  );
}

const HooksStyleComponent = () => {
    const [color, setColor] = useState('white')

    changeColorBlack = () => {
      setColor('black')
    }

    return(
      <div style={{ 
            padding: '10px',
            backgroundColor: color }}>
        <h1>Hello World</h1>
        <button onClick={this.changeColorBlack} >Dark </button>
      </div>
    )
}

export default App;
