import React from 'react';
import { createStore } from 'redux';

const reducer = (state = 0, action) => {

  // if (action.type === 'INC') {
  //   return state + 1;
  // }


  switch (action.type) {
    case 'INC':
      return state + 1;
    case 'DECK':
      return state - 1;

      default: // если не распознали наше действие то по дефолту возвращаем state 
        return state;
  }

}

const store = createStore(reducer)
const update = () => {
  store.getState
}
store.subscribe(update)

function App() {
  return (
    <div className='jumbotron'>
      <h2> 0 </h2>

      <button
        className='btn btn-primary btn-lg'
        onClick={()=> store.dispatch({type:'DECK'})} > DECK </button>

      <button 
        className='btn btn-primary btn-lg'
        onClick={()=> store.dispatch({type:'INC'})}>INC</button>
    </div>
  );
}

export default App;
