import React from 'react';

const initialState = 0;

const reducer = (state = 0, action) => {

  // if (action.type === 'INC') {
  //   return state + 1;
  // }


  switch (action.type) {
    case 'INC':
      return state + 1;

      default: // если не распознали наше действие то по дефолту возвращаем state 
        return state;
  }

}

let state = reducer (undefined, {})

state = reducer( state, {type: 'INC'} )


function App() {
  return (
    <div>
     <p></p>
     <p></p>
    </div>
  );
}

export default App;
