import React from 'react';
import { createStore } from 'redux';

const reducer = (state = 0, action) => {

  switch (action.type) {
    case 'INC':
      return state + 1;
    case 'DECK':
      return state - 1;

      case 'RND':
        return state + action.value;  

      default: // если не распознали наше действие то по дефолту возвращаем state 
        return state;
  }
}

const store = createStore(reducer)

const update = () => {
return (
  store.getState()
)
}

store.subscribe(update)

const value = Math.floor(Math.random() * 10)

const App = () => {

  return (
    <div className='jumbotron'>

    <h2>{store.getState()}</h2>

      <button
        className='btn btn-primary btn-lg'
        onClick={()=> store.dispatch(dec())} > DECK </button>

      <button 
        className='btn btn-primary btn-lg'
        onClick={()=> store.dispatch(inc())}>INC</button>

      <button 
        className='btn btn-primary btn-lg'
        onClick={()=> 
        store.dispatch( rnd(value) )}>RND</button>
    </div>
  );
}

export default App;
