import React from 'react';
import { createStore } from 'redux';
import reducer from './reducer'
import {dec , inc, rnd} from './actions'

const store = createStore(reducer)
const { dispatch, getState} = store

const decDispath = () => dispatch(dec())
const incDispath = () => dispatch(inc())
const rndDispath = (value) => dispatch(rnd(value))

const update = () => {
return (
  store.getState()
)
}

store.subscribe(update)

const value = Math.floor(Math.random() * 10)

const App = () => {

  return (
    <div className='jumbotron'>

    <h2>{getState()}</h2>

      <button
        className='btn btn-primary btn-lg'
        onClick={()=> decDispath} > DECK </button>

      <button 
        className='btn btn-primary btn-lg'
        onClick={()=> incDispath}>INC</button>

      <button 
        className='btn btn-primary btn-lg'
        onClick={()=> 
          rndDispath(value)}>RND</button>
    </div>
  );
}

export default App;
