const reducer = (state = 0, action) => {

    switch (action.type) {
      case 'INC':
        return state + 1;
      case 'DECK':
        return state - 1;
  
        case 'RND':
          return state + action.value;  
  
        default: // если не распознали наше действие то по дефолту возвращаем state 
          return state;
    }
  }

export default reducer;
