import React from 'react';
import { connect } from 'react-redux'

import {bookAddedToCart, allBooksRemovedFromCart, bookRemovedFromCart} from '../../actions/actions'

import './shop-cart-table.css'

const ShopCartTable = ({items, total, onIncrease, onDecrease, onDelete}) => {


    const renderRow = (item, idx) => {

    const {id, title, count, total} = item;

        return (
            <tr key={id}>
                <td>{idx + 1}</td>
                <td>{title}</td>
                <td>{count}</td>
                <td>${total}</td>
                <td>
                    <button
                        onClick={()=> onDelete(id)} 
                        className="btn btn-outline-danger btn-sm float-right">
                        <i className="fa fa-trash-o" />
                    </button>
                    <button
                        onClick={()=> onIncrease(id)}
                        className="btn btn-outline-success btn-sm float-right">
                        <i className="fa fa-plus-circle" />
                    </button>
                    <button
                        onClick={()=> onDecrease(id)}
                        className="btn btn-outline-warning btn-sm float-right">
                        <i className="fa fa-minus-circle" />
                    </button>
                </td>
            </tr>
        )
    }

    return (
        <div className="shopping-cart-table">
            <h2>Your order</h2>
            <table className='table'>
                <thead>
                    <tr>
                        <th>#</th>
                        <th>Item</th>
                        <th>Count</th>
                        <th>Price</th>
                        <th>Action</th>
                    </tr>
                </thead>

                <tbody>
                { items.map(renderRow) }
                </tbody>
            </table>

            <div className="total">
                Total: ${total}
            </div>

        </div>
    )
}

const mapStateToProps = (state) => {
    return {
        items: state.cartItems,
        total: state.orderTotal
    }
}

const mapDispatchToProps = () => {
    return {
        onIncrease: (id)=> {
            console.log(`increase ${id}`)
        },
        onDecrease: (id)=> {
            console.log(`onDecrease ${id}`)
        },
        onDelete: (id)=> {
            console.log(`onDelete ${id}`)
        }
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(ShopCartTable)
