const updateCartItems = (cartItems, item, idx) => {
    
    if (item.count === 0){
        return [
            ...cartItems.slice(0,idx),
            ...cartItems.slice(idx + 1)
        ]
    }

    if (idx === -1){
        return [
            ...cartItems,
            item
        ]
    }

    return [
        ...cartItems.slice(0,idx),
        item,
        ...cartItems.slice(idx + 1)
    ]
}

const updateCartItem = (book, item = {}, quantity) => {

    const {
        id = book.id, 
        count = 0,
        title = book.title,
        total = 0
    } = item;

    return {
        id,
        title,
        count: count + quantity,
        total: total + quantity*book.price
    }

