const initialState = { 
    books: [
        {   id:1, 
            name: 'english papa',
            author: 'Susan J'},

        {   id:2, 
            name: 'english mama',
            author: 'Joe Pi'}
    ]
}

const reducer = (state = initialState, action) => {
    switch (action.type) {
        case 'BOOKS_LOADED':
          return {
              books: action.payload
          }

        default:
          return state;
      }
}

export default reducer;
