const booksLoaded = (newBooks) => {
    return {
        type: 'BOOKS_LOADED',
        payload: newBooks
    }
}

const booksReuested = () => {
    return {
        type: 'BOOKS_REQUESTED'
    }
}

const booksError = (error) => {
    return {
        type: 'BOOKS_ERROR',
        payload: error
    }
}

fetchBooks: () => {
    dispatch(booksReuested()) // loading 
    bookstoreService.getBooks()
        .then((data) => {
            dispatch(booksLoaded(data))
        })
        .catch((err) => dispatch(booksError(err)))
}

export {
    booksLoaded,
    booksReuested,
    booksError
    
}
